const tokenAddress = '0xYourTokenContractAddress';

// Global variables
let web3;
let tokenContract;
let userAccount;
let chainId;

// DOM Elements
const connectWalletBtn = document.getElementById('connectWallet');
const statusDiv = document.getElementById('status');
const accountDiv = document.getElementById('account');
const networkDiv = document.getElementById('network');
const tokenNameSpan = document.getElementById('tokenName');
const tokenSymbolSpan = document.getElementById('tokenSymbol');
const tokenBalanceSpan = document.getElementById('tokenBalance');
const transferForm = document.getElementById('transferForm');
const recipientInput = document.getElementById('recipient');
const amountInput = document.getElementById('amount');
const transferButton = document.getElementById('transferButton');
const transferAlert = document.getElementById('transferAlert');
const transferSuccess = document.getElementById('transferSuccess');
const transactionsList = document.getElementById('transactions');

// Initialize the DApp
async function init() {
  // Check if MetaMask is installed
  if (window.ethereum) {
    try {
      // Request account access if needed
      connectWalletBtn.addEventListener('click', connectWallet);

      // Listen for account changes
      window.ethereum.on('accountsChanged', handleAccountsChanged);

      // Listen for chain changes
      window.ethereum.on('chainChanged', () => {
        window.location.reload();
      });

      // Setup form submission
      transferForm.addEventListener('submit', handleTransfer);

      // Check if already connected
      const accounts = await window.ethereum.request({ method: 'eth_accounts' });
      if (accounts.length > 0) {
        handleAccountsChanged(accounts);
      }
    } catch (error) {
      console.error("Error initializing DApp:", error);
      statusDiv.textContent = 'Error initializing DApp';
    }
  } else {
    // No web3 provider detected
    statusDiv.textContent = 'Please install MetaMask to use this DApp';
    connectWalletBtn.disabled = true;
  }
}

// Connect wallet function
async function connectWallet() {
  try {
    const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
    handleAccountsChanged(accounts);
  } catch (error) {
    console.error("Could not connect to wallet:", error);
    if (error.code === 4001) {
      // User rejected the request
      statusDiv.textContent = 'Connection rejected by user';
    } else {
      statusDiv.textContent = 'Error connecting to wallet';
    }
  }
}

// Handle account changes
function handleAccountsChanged(accounts) {
  if (accounts.length === 0) {
    statusDiv.textContent = 'Please connect your wallet';
    accountDiv.textContent = 'Account: Not connected';
    userAccount = null;
    connectWalletBtn.style.display = 'block';
    disableInterface();
  } else {
    userAccount = accounts[0];
    statusDiv.textContent = 'Connected to Ethereum network';
    accountDiv.textContent = `Account: ${userAccount.substring(0, 6)}...${userAccount.substring(38)}`;
    connectWalletBtn.style.display = 'none';
    enableInterface();
    setupWeb3();
  }
}

// Setup Web3 and contract
async function setupWeb3() {
  // Initialize web3 with the injected provider
  web3 = new Web3(window.ethereum);

  // Get network/chain ID
  chainId = await web3.eth.getChainId();
  let networkName;

  switch (chainId) {
    case 1:
      networkName = 'Ethereum Mainnet';
      break;
    case 3:
      networkName = 'Ropsten Testnet';
      break;
    case 4:
      networkName = 'Rinkeby Testnet';
      break;
    case 5:
      networkName = 'Goerli Testnet';
      break;
    case 42:
      networkName = 'Kovan Testnet';
      break;
    case 56:
      networkName = 'Binance Smart Chain';
      break;
    case 137:
      networkName = 'Polygon Mainnet';
      break;
    default:
      networkName = `Chain ID: ${chainId}`;
  }

  networkDiv.textContent = `Network: ${networkName}`;

  // Initialize contract
  tokenContract = new web3.eth.Contract(tokenABI, tokenAddress);

  // Load token information
  await loadTokenInfo();

  // Load past events
  loadPastTransfers();
}

// Load token information
async function loadTokenInfo() {
  try {
    // Get token name
    const name = await tokenContract.methods.name().call();
    tokenNameSpan.textContent = name;

    // Get token symbol
    const symbol = await tokenContract.methods.symbol().call();
    tokenSymbolSpan.textContent = symbol;

    // Update user balance
    updateBalance();
  } catch (error) {
    console.error("Error loading token info:", error);
    tokenNameSpan.textContent = 'Error loading';
    tokenSymbolSpan.textContent = 'Error loading';
  }
}

// Update token balance
async function updateBalance() {
  if (!userAccount) return;

  try {
    const balance = await tokenContract.methods.balanceOf(userAccount).call();
    // Convert from wei to ether-like units (assuming 18 decimals like most tokens)
    const formattedBalance = web3.utils.fromWei(balance, 'ether');
    tokenBalanceSpan.textContent = `${formattedBalance} tokens`;
  } catch (error) {
    console.error("Error fetching balance:", error);
    tokenBalanceSpan.textContent = 'Error loading balance';
  }
}

// Handle token transfer
async function handleTransfer(event) {
  event.preventDefault();

  // Hide previous messages
  transferAlert.style.display = 'none';
  transferSuccess.style.display = 'none';

  const recipient = recipientInput.value.trim();
  const amount = amountInput.value.trim();

  // Basic validation
  if (!web3.utils.isAddress(recipient)) {
    showError('Invalid recipient address');
    return;
  }

  if (amount <= 0) {
    showError('Amount must be greater than 0');
    return;
  }

  // Disable button during transaction
  transferButton.disabled = true;
  transferButton.textContent = 'Processing...';

  try {
    // Convert amount to wei (token decimal units)
    const amountInWei = web3.utils.toWei(amount, 'ether');

    // Send transaction
    const receipt = await tokenContract.methods.transfer(recipient, amountInWei).send({
      from: userAccount
    });

    // Show success message
    transferSuccess.textContent = `Transaction successful! Hash: ${receipt.transactionHash.substring(0, 10)}...`;
    transferSuccess.style.display = 'block';

    // Clear form
    recipientInput.value = '';
    amountInput.value = '';

    // Update balance
    updateBalance();

    // Add to transaction history
    addTransactionToHistory(receipt.transactionHash, recipient, amount);
  } catch (error) {
    console.error("Transfer error:", error);
    showError('Transaction failed: ' + (error.message || 'Unknown error'));
  } finally {
    transferButton.disabled = false;
    transferButton.textContent = 'Send Tokens';
  }
}

// Show error message
function showError(message) {
  transferAlert.textContent = message;
  transferAlert.style.display = 'block';
}

// Add transaction to history
function addTransactionToHistory(hash, to, amount) {
  const listItem = document.createElement('li');
  listItem.innerHTML = `<strong>Sent ${amount} tokens</strong> to ${to.substring(0, 6)}...${to.substring(38)} <br>
                       <a href="https://etherscan.io/tx/${hash}" target="_blank">View on Etherscan</a>`;
  transactionsList.prepend(listItem);
}

// Load past transfers
async function loadPastTransfers() {
  try {
    // Get past Transfer events where current user was sender or receiver
    const pastEvents = await tokenContract.getPastEvents('Transfer', {
      filter: {
        $or: [
          { from: userAccount },
          { to: userAccount }
        ]
      },
      fromBlock: chainId === 1 ? 'latest' : 0, // On mainnet, only get recent blocks to avoid long loading
      toBlock: 'latest'
    });

    // Clear existing list
    transactionsList.innerHTML = '';

    // Add events to the list
    pastEvents.forEach(event => {
      const { from, to, value } = event.returnValues;
      const formattedValue = web3.utils.fromWei(value, 'ether');
      const listItem = document.createElement('li');

      if (from === userAccount) {
        listItem.innerHTML = `<strong>Sent ${formattedValue} tokens</strong> to ${to.substring(0, 6)}...${to.substring(38)} <br>
                           <a href="https://etherscan.io/tx/${event.transactionHash}" target="_blank">View on Etherscan</a>`;
      } else {
        listItem.innerHTML = `<strong>Received ${formattedValue} tokens</strong> from ${from.substring(0, 6)}...${from.substring(38)} <br>
                           <a href="https://etherscan.io/tx/${event.transactionHash}" target="_blank">View on Etherscan</a>`;
      }

      transactionsList.appendChild(listItem);
    });

    if (pastEvents.length === 0) {
      transactionsList.innerHTML = '<li>No transactions found</li>';
    }
  } catch (error) {
    console.error("Error loading past transfers:", error);
    transactionsList.innerHTML = '<li>Error loading transaction history</li>';
  }
}

// Enable or disable interface based on connection status
function enableInterface() {
  transferButton.disabled = false;
  recipientInput.disabled = false;
  amountInput.disabled = false;
}

function disableInterface() {
  transferButton.disabled = true;
  recipientInput.disabled = true;
  amountInput.disabled = true;
  tokenNameSpan.textContent = 'Not connected';
  tokenSymbolSpan.textContent = 'Not connected';
  tokenBalanceSpan.textContent = 'Not connected';
}

// Initialize the DApp when the page loads
window.addEventListener('load', init);
